module.exports = (digit) => {
    /**
    * Method: otpGenerate
    * Purpose: To otp generate
    * @param {*} digit
    * @response {*} otp
    */
    let otp = Math.floor(Math.pow(10, (digit - 1)) + Math.random() * (9 * Math.pow(10, (digit - 1))));
    /**
    * Generates a random secret with the set 0-9 and symbols, of any length
    */
    return otp;
}